mapper.select:
select count(*)
  from tbl_member
 where member_id = #{_parameter}

mapper.update:
update tbl_member
   set member_name = #{memberName},
       member_phone = #{memberPhone}
 where member_id = #{memberId}

update tbl_member
   set member_pw = #{memberPw}
 where member_id = #{memberId}
